const prompt = require("prompt-sync")();
let i = 1
const x = Number(prompt("Digite um número maior que 0 para descobrir se ele é primo: "));
let contador = 0
do {
 if(x % i == 0){
    contador++;
 }
 i++;
} while( i<= x)

if(contador > 2){
    console.log("Não é primo!")
} else {
    console.log("É PRIMO!")
}