//Receba um número pelo prompt e crie um loop do...while que calcule o fatorial desse número.

const prompt = require("prompt-sync")();
let i = 1
let numero = Number(prompt("Digite um número maior que 0 para saber seu fatorial: "))

let fatorial = numero

do{
    fatorial = fatorial * i
    i++
} while ( i < numero)
console.log(fatorial)